import React, { useState } from "react";

export const User = ({ name, email, id, onEdit, onDelete }) => {
  const [isEdit, setIsEdit] = useState(false);

  const handleEdit = () => {
    setIsEdit(!isEdit);
  };

  const handleDelete = () => {
    onDelete(id);
  };

  const handleOnEditSubmit = (evt) => {
    evt.preventDefault();
    onEdit(id, evt.target.name.value, evt.target.email.value);
    setIsEdit(!isEdit);
  };

  return (
    <div>
      {isEdit ? (

                <form className="formstyle" onSubmit={handleOnEditSubmit}>
                  <input placeholder="Name" name="name" defaultValue={name} />
                  <input placeholder="Email" name="email" defaultValue={email} />
                  <button onSubmit={handleOnEditSubmit}>Save</button>
                </form>
        
      ) : (
        <div className="user">
          <span className="user-name">{name}</span>
          <span className="user-email">{email}</span>
          <div>
          <button onClick={handleEdit}><span className="button-text">DETAY</span></button>
            <button className="duzenle-button" onClick={handleEdit}><span className="button-text">DÜZENLE</span></button>
            <button onClick={handleDelete}><span className="button-text">SİL</span></button>
          </div>
        </div>
      )}
    </div>
  );
};
